SELECT id,account,full_name as fullName ,role_name as roleName,DATE_FORMAT(create_time,'%y-%d-%m %H:%i%s') as createTime,DATE_FORMAT(update_time,'%y-%d-%m %H:%i%s') as updateTime
FROM conf_account
where id = #{id}

SELECT id,account,full_name as fullName ,role_name as roleName,DATE_FORMAT(create_time,'%y-%d-%m %H:%i%s') as createTime,DATE_FORMAT(update_time,'%y-%d-%m %H:%i%s') as updateTime
FROM conf_account


        AND account = #{account}


        AND full_name = #{fullName}

SELECT count(id)
FROM conf_account


        AND account = #{account}


        AND full_name = #{fullName}
