mapper.update:
UPDATE
device
SET
device_id=#{deviceId}
WHERE open_id = #{openId} and game_id=#{gameId}

UPDATE
device
SET
total_scores = scores+#{scores},
scores=scores+#{scores}
WHERE open_id = #{openId} and game_id = #{gameId}

UPDATE
device
SET
scores=scores+#{scores}
WHERE open_id = #{openId} and game_id = #{gameId}

UPDATE
device
SET
scores=scores+#{scores}
WHERE open_id = #{openId} and game_id = #{gameId}

mapper.select:
SELECT count(id)
FROM device
where open_id = #{openId} and device_id = #{deviceId} and game_id = #{gameId}
