mapper.select:
SELECT open_id as openId,game_id as gameId,price,type,destroy_price as destroyPrice,num,url1,url2,url3
FROM warehouse
where open_id = #{openId} and game_id = #{gameId} and type=#{type}

mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO warehouse
(
open_id,game_id,price,destroy_price,num,url1,url2,url3,type
)
VALUES
(
#{openId},#{gameId},#{price},#{destroyPrice},#{num},#{url1},#{url2},#{url3},#{type}
)

mapper.update:
UPDATE
warehouse
SET
num = # {num}
WHERE id = #{id}
